mapper.select:
SELECT member_id, name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate
FROM member
WHERE member_id = #{value}

mapper.update:
UPDATE member
SET password = #{passwd}
WHERE member_id = #{memberId}

mapper.select:
SELECT member_id, name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate FROM member
and id = #{value}
and name LIKE '%' || #{value} || '%'

SELECT member_id, name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate
FROM member


        or member_id = #{memberId}


        or name LIKE '%' || #{name} || '%'


    or email = #{email}
