mapper.select:
select member_id, password, name, hp, email, address, address2, address3
from member
where member_id = #{memberId} and password = #{password}

select member_id, password, name, hp, email, address, address2, address3
from member
where member_id = #{memberId}

mapper.update:
UPDATE member


        name = #{name},


        email = #{email}


        password = #{password}


        address = #{address}


        address2 = #{address2}


        address3 = #{address3}


        hp = #{hp}


WHERE member_id = #{memberId}
